const articles = $input.all();
const issues = [];
const seoChecks = [];

articles.forEach(article => {
  const item = article.json;
  const checks = {
    article_id: item.id,
    title: item.title,
    slug: item.slug,
    audit_date: new Date().toISOString(),
    issues_found: []
  };

  // VÃ©rifications SEO de base

  // 1. Meta description manquante ou trop courte/longue
  if (!item.meta_description) {
    checks.issues_found.push('missing_meta_description');
  } else if (item.meta_description.length < 120) {
    checks.issues_found.push('meta_description_too_short');
  } else if (item.meta_description.length > 160) {
    checks.issues_found.push('meta_description_too_long');
  }

  // 2. Titre trop court ou trop long
  if (!item.title) {
    checks.issues_found.push('missing_title');
  } else if (item.title.length < 30) {
    checks.issues_found.push('title_too_short');
  } else if (item.title.length > 60) {
    checks.issues_found.push('title_too_long');
  }

  // 3. Slug non optimisÃ©
  if (!item.slug) {
    checks.issues_found.push('missing_slug');
  } else if (item.slug.length > 75) {
    checks.issues_found.push('slug_too_long');
  }

  // 4. Contenu pas mis Ã  jour depuis longtemps
  const lastUpdate = new Date(item.updated_at);
  const monthsOld = (Date.now() - lastUpdate.getTime()) / (1000 * 60 * 60 * 24 * 30);
  if (monthsOld > 6) {
    checks.issues_found.push('content_outdated');
  }

  checks.total_issues = checks.issues_found.length;
  checks.seo_score = Math.max(0, 100 - (checks.total_issues * 20));

  seoChecks.push(checks);

  // Ajouter aux issues globales si problÃ¨me dÃ©tectÃ©
  if (checks.total_issues > 0) {
    issues.push({
      type: 'seo_issue',
      severity: checks.total_issues > 2 ? 'high' : 'medium',
      article_id: item.id,
      article_title: item.title,
      issues: checks.issues_found,
      seo_score: checks.seo_score
    });
  }
});

return [{
  json: {
    audit_summary: {
      total_articles: articles.length,
      articles_with_issues: issues.length,
      avg_seo_score: seoChecks.reduce((sum, check) => sum + check.seo_score, 0) / seoChecks.length,
      audit_date: new Date().toISOString()
    },
    seo_checks: seoChecks,
    issues: issues
  }
}];

const htmlContent = $input.first().json.data;
const technicalIssues = [];

if (typeof htmlContent === 'string') {
  // VÃ©rifications techniques

  // 1. Images sans attribut alt
  const imgRegex = /<img[^>]*>/gi;
  const images = htmlContent.match(imgRegex) || [];
  let imagesWithoutAlt = 0;

  images.forEach(img => {
    if (!img.includes('alt=') || img.includes('alt=""') || img.includes("alt=''")) {
      imagesWithoutAlt++;
    }
  });

  if (imagesWithoutAlt > 0) {
    technicalIssues.push({
      type: 'missing_alt_text',
      count: imagesWithoutAlt,
      severity: 'medium',
      description: `${imagesWithoutAlt} images sans attribut alt`
    });
  }

  // 2. Liens cassÃ©s internes (simulation)
  const linkRegex = /<a[^>]+href=["']([^"']+)["'][^>]*>/gi;
  const links = [];
  let match;

  while ((match = linkRegex.exec(htmlContent)) !== null) {
    links.push(match[1]);
  }

  const internalLinks = links.filter(link => 
    link.startsWith('/') || link.includes(process.env.WEBSITE_URL)
  );

  // 3. Meta tags manquants
  const metaIssues = [];
  if (!htmlContent.includes('<meta name="description"')) {
    metaIssues.push('missing_meta_description');
  }
  if (!htmlContent.includes('<meta property="og:title"')) {
    metaIssues.push('missing_og_title');
  }
  if (!htmlContent.includes('<meta property="og:description"')) {
    metaIssues.push('missing_og_description');
  }
  if (!htmlContent.includes('<meta property="og:image"')) {
    metaIssues.push('missing_og_image');
  }

  if (metaIssues.length > 0) {
    technicalIssues.push({
      type: 'missing_meta_tags',
      issues: metaIssues,
      severity: 'high',
      description: `${metaIssues.length} meta tags manquants`
    });
  }

  // 4. Structure H1/H2
  const h1Count = (htmlContent.match(/<h1[^>]*>/gi) || []).length;
  const h2Count = (htmlContent.match(/<h2[^>]*>/gi) || []).length;

  if (h1Count === 0) {
    technicalIssues.push({
      type: 'missing_h1',
      severity: 'high',
      description: 'Aucun H1 trouvÃ© sur la page'
    });
  } else if (h1Count > 1) {
    technicalIssues.push({
      type: 'multiple_h1',
      count: h1Count,
      severity: 'medium',
      description: `${h1Count} balises H1 trouvÃ©es (recommandÃ©: 1)`
    });
  }
}

return [{
  json: {
    technical_audit: {
      total_issues: technicalIssues.length,
      issues: technicalIssues,
      audit_date: new Date().toISOString(),
      page_url: process.env.WEBSITE_URL
    }
  }
}];

const contentAudit = $input.all()[0].json;
const technicalAudit = $input.all()[1].json;

// Combiner les rÃ©sultats
const combinedReport = {
  audit_type: 'content_seo_health_check',
  audit_date: new Date().toISOString(),

  // RÃ©sumÃ© global
  summary: {
    total_articles: contentAudit.audit_summary.total_articles,
    articles_with_issues: contentAudit.audit_summary.articles_with_issues,
    avg_seo_score: Math.round(contentAudit.audit_summary.avg_seo_score),
    technical_issues: technicalAudit.technical_audit.total_issues
  },

  // Score global (0-100)
  overall_health_score: Math.round(
    (contentAudit.audit_summary.avg_seo_score * 0.7) + 
    (Math.max(0, 100 - (technicalAudit.technical_audit.total_issues * 10)) * 0.3)
  ),

  // Recommandations prioritaires
  priority_actions: [],

  // DÃ©tails
  content_issues: contentAudit.issues,
  technical_issues: technicalAudit.technical_audit.issues,

  // MÃ©triques pour le dashboard
  metrics: {
    articles_missing_meta: contentAudit.issues.filter(i => 
      i.issues.includes('missing_meta_description')
    ).length,
    articles_outdated: contentAudit.issues.filter(i => 
      i.issues.includes('content_outdated')
    ).length,
    images_without_alt: technicalAudit.technical_audit.issues.find(i => 
      i.type === 'missing_alt_text'
    )?.count || 0
  }
};

// GÃ©nÃ©rer des recommandations
if (combinedReport.summary.articles_with_issues > 0) {
  combinedReport.priority_actions.push(
    `Optimiser ${combinedReport.summary.articles_with_issues} articles avec des problÃ¨mes SEO`
  );
}

if (combinedReport.metrics.articles_missing_meta > 0) {
  combinedReport.priority_actions.push(
    `Ajouter des meta descriptions Ã  ${combinedReport.metrics.articles_missing_meta} articles`
  );
}

if (combinedReport.metrics.images_without_alt > 0) {
  combinedReport.priority_actions.push(
    `Ajouter des attributs alt Ã  ${combinedReport.metrics.images_without_alt} images`
  );
}

// DÃ©finir le niveau de santÃ©
if (combinedReport.overall_health_score >= 90) {
  combinedReport.health_status = 'excellent';
} else if (combinedReport.overall_health_score >= 75) {
  combinedReport.health_status = 'good';
} else if (combinedReport.overall_health_score >= 60) {
  combinedReport.health_status = 'needs_improvement';
} else {
  combinedReport.health_status = 'critical';
}

return [{ json: combinedReport }];

const report = $json;

const alertMessage = `ðŸ” RAPPORT SANTÃ‰ SEO HEBDOMADAIRE

ðŸ“Š Score global: ${report.overall_health_score}/100 (${report.health_status})

ðŸ“ Articles analysÃ©s: ${report.summary.total_articles}
âš ï¸ Articles avec problÃ¨mes: ${report.summary.articles_with_issues}
ðŸ”§ ProblÃ¨mes techniques: ${report.summary.technical_issues}

ðŸŽ¯ ACTIONS PRIORITAIRES:
${report.priority_actions.map(action => `â€¢ ${action}`).join('
')}

ðŸ“… Audit rÃ©alisÃ© le: ${new Date(report.audit_date).toLocaleString('fr-FR', { timeZone: 'Europe/Paris' })}

${report.overall_health_score < 60 ? 'ðŸš¨ ATTENTION: Score critique, action immÃ©diate requise!' : 'ðŸ’¡ Optimisations recommandÃ©es pour amÃ©liorer le rÃ©fÃ©rencement'}`;

return [{
  json: {
    alert_type: 'seo_health_report',
    severity: report.overall_health_score < 60 ? 'critical' : 'warning',
    message: alertMessage,
    score: report.overall_health_score,
    status: report.health_status
  }
}];
