const monitoringData = $input.all()[0].json || [];
const systemData = $input.all()[1]?.json?.stdout || '';
const nginxLogs = $input.all()[2]?.json?.stdout || '';

// Analyse des performances
const performanceIssues = [];
const securityAlerts = [];

// 1. Analyse des temps de rÃ©ponse
if (Array.isArray(monitoringData)) {
  const avgResponseTimes = {};
  const downtime = {};

  monitoringData.forEach(record => {
    const url = record.url_checked;
    if (!avgResponseTimes[url]) {
      avgResponseTimes[url] = [];
      downtime[url] = 0;
    }

    if (record.response_time) {
      avgResponseTimes[url].push(record.response_time);
    }

    if (!record.is_available) {
      downtime[url]++;
    }
  });

  // Alertes de performance
  Object.entries(avgResponseTimes).forEach(([url, times]) => {
    if (times.length > 0) {
      const avg = times.reduce((a, b) => a + b, 0) / times.length;
      if (avg > 3000) { // Plus de 3 secondes
        performanceIssues.push({
          type: 'slow_response',
          url: url,
          avg_response_time: Math.round(avg),
          severity: avg > 5000 ? 'critical' : 'warning'
        });
      }
    }
  });

  // Alertes de disponibilitÃ©
  Object.entries(downtime).forEach(([url, down_count]) => {
    if (down_count > 0) {
      securityAlerts.push({
        type: 'service_downtime',
        url: url,
        down_incidents: down_count,
        severity: 'critical'
      });
    }
  });
}

// 2. Analyse des ressources systÃ¨me
const systemAlerts = [];
if (systemData) {
  const lines = systemData.split('\n');

  // Analyse du disque
  lines.forEach(line => {
    if (line.includes('%') && line.includes('/dev/')) {
      const match = line.match(/(\d+)%/);
      if (match) {
        const usage = parseInt(match[1]);
        if (usage > 90) {
          systemAlerts.push({
            type: 'disk_space_critical',
            usage: usage,
            details: line.trim(),
            severity: 'critical'
          });
        } else if (usage > 80) {
          systemAlerts.push({
            type: 'disk_space_warning',
            usage: usage,
            details: line.trim(),
            severity: 'warning'
          });
        }
      }
    }
  });

  // Analyse des conteneurs Docker
  lines.forEach(line => {
    if (line.includes('%') && (line.includes('website') || line.includes('strapi') || line.includes('postgres'))) {
      const parts = line.split(/\s+/);
      if (parts.length >= 3) {
        const cpuMatch = parts[1]?.match(/(\d+\.\d+)%/);
        const memMatch = parts[3]?.match(/(\d+\.\d+)%/);

        if (cpuMatch && parseFloat(cpuMatch[1]) > 80) {
          systemAlerts.push({
            type: 'high_cpu_usage',
            container: parts[0],
            cpu_usage: parseFloat(cpuMatch[1]),
            severity: 'warning'
          });
        }

        if (memMatch && parseFloat(memMatch[1]) > 85) {
          systemAlerts.push({
            type: 'high_memory_usage',
            container: parts[0],
            memory_usage: parseFloat(memMatch[1]),
            severity: 'warning'
          });
        }
      }
    }
  });
}

// 3. Analyse des logs de sÃ©curitÃ©
if (nginxLogs) {
  const errorCount = parseInt(nginxLogs.split('\n')[0]) || 0;

  if (errorCount > 10) {
    securityAlerts.push({
      type: 'high_error_rate',
      error_count: errorCount,
      severity: errorCount > 50 ? 'critical' : 'warning',
      description: `${errorCount} erreurs HTTP dÃ©tectÃ©es dans les logs rÃ©cents`
    });
  }
}

// RÃ©sumÃ© global
const totalIssues = performanceIssues.length + securityAlerts.length + systemAlerts.length;
const criticalIssues = [...performanceIssues, ...securityAlerts, ...systemAlerts]
  .filter(issue => issue.severity === 'critical').length;

return [{
  json: {
    monitoring_report: {
      timestamp: new Date().toISOString(),
      total_issues: totalIssues,
      critical_issues: criticalIssues,
      status: criticalIssues > 0 ? 'critical' : (totalIssues > 0 ? 'warning' : 'healthy'),
      performance_issues: performanceIssues,
      security_alerts: securityAlerts,
      system_alerts: systemAlerts,
      system_health_score: Math.max(0, 100 - (criticalIssues * 30) - ((totalIssues - criticalIssues) * 10))
    }
  }
}];

const report = $json.monitoring_report;

let alertMessage = `ðŸ›¡ï¸ ALERTE SÃ‰CURITÃ‰ & PERFORMANCE\n\n`;
alertMessage += `ðŸ“Š Score de santÃ© systÃ¨me: ${report.system_health_score}/100\n`;
alertMessage += `âš ï¸ Total des problÃ¨mes: ${report.total_issues}\n`;
alertMessage += `ðŸš¨ ProblÃ¨mes critiques: ${report.critical_issues}\n\n`;

// DÃ©tailler les problÃ¨mes critiques
if (report.performance_issues.length > 0) {
  alertMessage += `ðŸŒ PROBLÃˆMES DE PERFORMANCE:\n`;
  report.performance_issues.forEach(issue => {
    if (issue.severity === 'critical') {
      alertMessage += `â€¢ ${issue.url}: ${issue.avg_response_time}ms (critique)\n`;
    }
  });
  alertMessage += `\n`;
}

if (report.security_alerts.length > 0) {
  alertMessage += `ðŸ”’ ALERTES DE SÃ‰CURITÃ‰:\n`;
  report.security_alerts.forEach(alert => {
    alertMessage += `â€¢ ${alert.type}: ${alert.description || alert.url}\n`;
  });
  alertMessage += `\n`;
}

if (report.system_alerts.length > 0) {
  alertMessage += `âš™ï¸ ALERTES SYSTÃˆME:\n`;
  report.system_alerts.forEach(alert => {
    if (alert.severity === 'critical') {
      alertMessage += `â€¢ ${alert.type}: ${alert.details || alert.container}\n`;
    }
  });
  alertMessage += `\n`;
}

alertMessage += `ðŸ“… VÃ©rification: ${new Date(report.timestamp).toLocaleString('fr-FR', { timeZone: 'Europe/Paris' })}\n\n`;
alertMessage += `ðŸ”§ Action immÃ©diate requise pour rÃ©soudre les problÃ¨mes critiques.`;

return [{
  json: {
    alert_type: 'security_performance',
    severity: 'critical',
    message: alertMessage,
    critical_count: report.critical_issues,
    health_score: report.system_health_score
  }
}];
